  .el-dialog__header {
    padding-top: 60px;
    padding-left: 50px;
    pointer-events: none;

    .el-dialog__title {
      font-size: 28px;
      color: #ffffff;
    }
  }
  .el-dialog__body {
    padding: 30px 50px 42px;
